    ngOnInit(): void {
      this.homeservice.getprod()
      .subscribe(res=>{
        this.productList=res;

        //to show quantity and price 
        this.productList.forEach((a:any)=>{
          Object.assign(a,{quantity:1,total:a.price})

          //to show the no.when cart is added
