import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'filtercat'
})
export class FiltercatPipe implements PipeTransform {

  transform(value : any[], filtetString: string, category:string):any[] {
    const result:any=[];

    //if the value is empty
    if(!value|| filtetString==''||category==''){
    return value;
  }
  //if some value coming 
  value.forEach((a:any)=>{
    if(a[category].trim().toLowerCase().includes(filtetString.toLowerCase())){
      result.push(a);
  }
  });
  return result;
}

}
